function switchToSensor(){
    $("#logPage").show();
    $("#aboutPage").hide();
    $("#settingsPage").hide();
}

function switchToAbout(){
    $("#logPage").hide();
    $("#aboutPage").show();
    $("#settingsPage").hide();
}

function switchToSettings(){
    $("#logPage").hide();
    $("#aboutPage").hide();
    $("#settingsPage").show();
}


